mapper.select:
SELECT disaster_class.id AS classId, disaster_class.`name` AS className, disaster_class.principal, disaster_class.phone, disaster_class.peopleCount, disaster_area.id AS areaId, disaster_area.`name` AS areaName, disaster_area.forestType, area_landtype.landType, disaster_area.dominantTree, disaster_class.createDate FROM disaster_class LEFT JOIN disaster_area ON disaster_class.id = disaster_area.classId LEFT JOIN area_landtype ON disaster_area.landTypeId = area_landtype.id
and disaster_class.name like concat('%', #{className}, '%')
and disaster_area.id = #{areaId}
limit #{page}, #{limit}

SELECT count(*) FROM disaster_class LEFT JOIN disaster_area ON disaster_class.id = disaster_area.classId LEFT JOIN area_landtype ON disaster_area.landTypeId = area_landtype.id
and disaster_class.name like concat('%', #{className}, '%')
and disaster_area.id = #{areaId}
