mapper.select:
SELECT system_user.userId, system_user.userName, system_user.userPwd, system_role.roleName, system_user.realName FROM system_user INNER JOIN system_user_role ON system_user.userId = system_user_role.userId INNER JOIN system_role ON system_user_role.roleId = system_role.roleId WHERE system_user.isDeleted = 0
and system_role.roleId = #{roleId}
limit #{page}, #{limit}
